    AND id LIKE concat('%',#{infoUpdate.searchText,jdbcType=VARCHAR},'%')


    AND subTypeId IN (

        #{item}

    )


    AND (
    id LIKE concat('%',#{infoUpdate.searchText,jdbcType=VARCHAR},'%')
    OR
    subTypeId IN (

        #{item}

    )
    )


    AND issueTime

    >

    #{infoUpdate.beginDateTime,jdbcType=DATE}


    AND issueTime

    <

    #{infoUpdate.endDateTime,jdbcType=DATE}

