mapper.select:
select
  md5
from linkcontentstats
  where siteId = #{siteId}
  and typeId = #{typeId}
  and url = #{url}
  order by checkTime desc
  limit 0,1

select
  md5
from linkcontentstats
  where siteId = #{siteId}
  and typeId = #{typeId}
  and url = #{url}
  order by checkTime desc
  limit 1,1
